function get_web_search_results(params, userSettings) {
  var keyword = params.keyword;
  var cx = userSettings.searchEngineID;
  var key = userSettings.searchEngineAPIKey;

  if (!cx || !key) {
    throw new Error(
      'Please set the Search Engine ID and API Key in the plugin settings.'
    );
  }

  return fetch(
    `https://customsearch.googleapis.com/customsearch/v1?q=${encodeURIComponent(
      keyword
    )}&key=${key}&cx=${cx}`
  )
    .then((r) => r.json())
    .then((response) => {
      if (response.error) {
        throw new Error('Error: ' + response.error.message);
      }
      const items = response.items;

      if (!items) {
        return 'No results found';
      }

      return items
        .map(
          (item) => `
Title: ${item.title}
Result: ${item.snippet}
URL: ${item.link}
 `
        )
        .join('');
    });
}

function escapeAlt(text) {
  if (!text) {
    return '';
  }

  return text
    .replace(/\\/g, '\\\\') // backslash first
    .replace(/\n/g, ' ') // no newlines in alt text
    .replace(/\[/g, '\\[') // escape [ and ]
    .replace(/\]/g, '\\]')
    .replace(/</g, '&lt;') // defensive: HTML-sensitive chars
    .replace(/>/g, '&gt;');
}

async function createOpenAIURL(prompt, openaikey, quality, resolution) {
  var requestHeaders = new Headers();
  requestHeaders.append('Content-Type', 'application/json');
  requestHeaders.append('Authorization', 'Bearer ' + openaikey);

  var raw = JSON.stringify({
    model: 'dall-e-3',
    prompt: prompt,
    n: 1,
    size: resolution,
    quality: quality,
    response_format: 'url',
  });

  var requestOptions = {
    method: 'POST',
    headers: requestHeaders,
    body: raw,
    redirect: 'follow',
  };

  let response = await fetch(
    'https://api.openai.com/v1/images/generations',
    requestOptions
  );
  if (response.status === 401) {
    throw new Error('Invalid OpenAI API Key. Please check your settings.');
  }

  if (!response.ok) {
    const errorText = await response.text();
    throw new Error(errorText);
  }

  let data = await response.json();

  let url = data.data[0].url;
  const alt = escapeAlt(prompt);

  let endresult = '![' + alt + '](' + url + ')';
  return endresult;
}

async function generateOpenAIResult(prompts, openaikey, quality, resolution) {
  const results = await Promise.all(
    prompts.map((prompt) =>
      createOpenAIURL(prompt, openaikey, quality, resolution)
    )
  );
  return (
    results.join('\n\n') +
    '\n_Note: Generated images will expire after 1 hour. Save them to your device if you wish to keep the images._'
  );
}

async function image_generation_via_dalle_3(params, userSettings) {
