  if (userSettings.researchMode === 'Comprehensive mode') {
    return await fetch('https://api.firecrawl.dev/v2/scrape', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + userSettings.firecrawlApiKey,
      },
      body: JSON.stringify({
        url: params.pageUrl,
        formats: ['markdown'],
        onlyMainContent: true,
      }),
    }).then((r) => r.json());
  } else {
    const json = await fetch('https://api.firecrawl.dev/v2/scrape', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + userSettings.firecrawlApiKey,
      },
      body: JSON.stringify({
        url: params.pageUrl,
        formats: [
          {
            type: 'json',
            prompt: params.extractInstructions,
          },
        ],
      }),
    }).then((r) => r.json());

    if (userSettings.researchMode === 'Dynamic mode') {
      return {
        result: json,
        furtherInstructions:
          'This result is extracted from the original web page. Use the "read_full_web_page_content" tool if you need to read the full content of the web page.',
      };
    } else {
      return json;
    }
  }
}

